import React, {memo, useCallback, useEffect, useMemo, useRef} from 'react';
import {isEqualReact} from "@react-hookz/deep-equal";
import {Editor, NodeViewWrapper} from "@tiptap/react";
import {Image} from "@nextui-org/react";
import clsx from "clsx";

interface ImageBlockViewProps {
    editor: Editor
    getPos: () => number
    node: Node & {
        attrs: {
            src: string;
            align?: string;
            width?: string;
        }
    }
    updateAttributes: (attrs: Record<string, string>) => void
}

const FallBackImage = "data:image/png;base64,";

const ImageBlockNode = (props: ImageBlockViewProps) => {
    const {editor, getPos, node} = props
    const imageWrapperRef = useRef<HTMLDivElement>(null)
    const {src} = node.attrs;

    const wrapperClassName = clsx(
        node.attrs.align === 'left' ? 'ml-0' : 'ml-auto',
        node.attrs.align === 'right' ? 'mr-0' : 'mr-auto',
        node.attrs.align === 'center' && 'mx-auto',
    )

    const onClick = useCallback(() => {
        editor.commands.setNodeSelection(getPos())
    }, [getPos, editor.commands])

    const $src = useMemo(() => {
        if (!src) return FallBackImage
        return src;
    }, [src])

    return (
        <NodeViewWrapper>
            <div className={clsx("border-2 border-zinc-300 rounded-2xl",wrapperClassName)} style={{ width: node.attrs.width }}>
                <div contentEditable={false} ref={imageWrapperRef} className="w-full">
                    <Image
                        isZoomed
                        isBlurred
                        width={"100%"}
                        className="block"
                        classNames={{
                            wrapper: "w-full",
                            img: "w-full"
                        }}
                        src={$src}
                        fallbackSrc={FallBackImage}
                        onClick={onClick}
                    />
                </div>
            </div>
        </NodeViewWrapper>
    );
}

export default ImageBlockNode;
